<script>
import { db } from '@/firebase.js';
import { ref, set } from 'firebase/database';

export default {
  data() {
    return {
      selectedDate: '',
      notificationMessage: '',
      notificationType: '',
    };
  },
  methods: {
    setNotification(type, message) {
      this.notificationType = type;
      this.notificationMessage = message;
    },

    async handleMoodClick(mood) {
      if (!this.selectedDate) {

        console.log('W채hle zuerst ein Datum aus, zur Speicherung der Stimmung:', mood);
        this.setNotification('warning', `Das Datum fehlt! W채hle zuerst ein Datum aus, zur Speicherung der Stimmung: ${mood}`);
        return;
      }

      try {
        await this.addMoodToDatabase(mood, this.selectedDate);
        console.log('Stimmung erfolgreich gespeichert.');
        this.setNotification('success', `Stimmung erfolgreich gespeichert. Ausgew채hlte Stimmung: ${mood}, Ausgew채hltes Datum: ${this.selectedDate}`);
      } catch (error) {
        console.error('Fehler beim Speichern der Stimmung:', error);
        this.setNotification('error', `Fehler beim Speichern der Stimmung: ${error.message}`);
      }
    },

    async addMoodToDatabase(mood, selectedDate) {
      const moodsRef = ref(db, `moods/${selectedDate}`);

      await set(moodsRef, {
        mood: mood,
      });
    },
  },
};
</script>

